sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.imports
 */

function evaluateProperty({
    api
}) {
    if (!api.data.glide_form_1.nowRecordFormBlob) {} else {
        if (api.data.glide_form_1.nowRecordFormBlob && api.data.glide_form_1.nowRecordFormBlob.fields && api.data.document_approval_internationalization_support_1 && api.data.document_approval_internationalization_support_1.output) {
            var document_form_fields = api.data.glide_form_1.nowRecordFormBlob.fields;
            document_form_fields.name.label = api.data.document_approval_internationalization_support_1.output['document_approval_form_name'];
            document_form_fields.description.label = api.data.document_approval_internationalization_support_1.output['document_approval_form_description'];
            document_form_fields.approval_category.label = api.data.document_approval_internationalization_support_1.output['document_approval_form_category'];
            return document_form_fields;
        } else{
            return '';
        }
    }
}
