sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.imports
 */
function evaluateProperty({
    api
}) {
    if (api.data.glide_form_1.nowRecordFormBlob) {
        var sections = api.data.glide_form_1.nowRecordFormBlob.sections;
        if (sections.length > 0 && api.data.document_approval_internationalization_support_1 && api.data.document_approval_internationalization_support_1.output) {
            sections[0].label = api.data.document_approval_internationalization_support_1.output['document_approval_form_title'];

            return sections;
        } else {
            return '';
        }
    }
}
